import React from "react";
import ManagerDashboard from "./ManagerDashboard";
import TeamLeadDashboard from "./TeamLeadDashboard";
import EmployeeDashboard from "./EmployeeDashboard";

function Dashboard({ user }) {
  const role = String(user?.role || "").toLowerCase();

  const dashboards = {
    manager: ManagerDashboard,
    "team lead": TeamLeadDashboard,
    teamlead: TeamLeadDashboard,
    lead: TeamLeadDashboard,
    employee: EmployeeDashboard,
    staff: EmployeeDashboard,
  };

  const Selected = dashboards[role] || EmployeeDashboard;

  if (typeof Selected !== "function") {
    console.error("Invalid dashboard component for role:", role, Selected);
    return (
      <div className="container py-4">
        <h2 className="mb-2">Dashboard</h2>
        <div className="alert alert-danger">
          Unable to load dashboard for role <b>{role || "unknown"}</b>. Check imports/exports.
        </div>
      </div>
    );
  }

  return <Selected user={user} />;
}

export default Dashboard;
