import React, { useEffect, useMemo, useState } from 'react';

function ManagerDashboard({ user }) {
  const [allShifts, setAllShifts] = useState([]);
  const [pendingLeaves, setPendingLeaves] = useState([]);
  const [pendingSwaps, setPendingSwaps] = useState([]);
  const [newShift, setNewShift] = useState({ userId: "", date: "" });
  const [allUsers, setAllUsers] = useState([]);

  const BASE = process.env.REACT_APP_API_BASE_URL || "";

  // ---------- Helpers ----------
  const userMap = useMemo(() => {
    const m = {};
    for (const u of allUsers) {
      const name =
        u?.name ||
        u?.fullName ||
        u?.username ||
        u?.email ||
        'User';
      if (u?._id) m[u._id] = name;
      if (u?.id)  m[u.id]  = name; // just in case
    }
    return m;
  }, [allUsers]);

  const dateStr = (d) => (d ? new Date(d).toLocaleDateString() : "");

  // Resolve a user's display name from various possible shapes
  const nameFrom = (item) => {
    // Try nested user objects first
    const u =
      item?.user ||
      item?.employee ||
      item?.owner ||
      null;

    if (u?.name || u?.fullName || u?.username || u?.email) {
      return u.name || u.fullName || u.username || u.email;
    }

    // Try direct string or id fields
    const possibleIds = [
      item?.userId,
      item?.employeeId,
      item?.ownerId,
      typeof item?.user === 'string' ? item.user : null,
    ].filter(Boolean);

    for (const id of possibleIds) {
      if (userMap[id]) return userMap[id];
    }

    // Try loose field names
    return (
      item?.userName ||
      item?.employeeName ||
      item?.createdByName ||
      'User'
    );
  };

  // Resolve swap participant name (initiator/target)
  const swapName = (obj, idKey) => {
    if (obj?.name || obj?.fullName || obj?.username || obj?.email) {
      return obj.name || obj.fullName || obj.username || obj.email;
    }
    if (obj?._id && userMap[obj._id]) return userMap[obj._id];
    if (idKey && userMap[idKey]) return userMap[idKey];
    return 'User';
  };

  // ---------- Data load ----------
  useEffect(() => {
    const t = localStorage.getItem('token');
    const headers = { 'Authorization': `Bearer ${t}` };

    fetch(`${BASE}/api/shifts`, { headers })
      .then(r => r.json())
      .then(setAllShifts)
      .catch(console.error);

    fetch(`${BASE}/api/leaves/pending`, { headers })
      .then(r => r.json())
      .then(setPendingLeaves)
      .catch(console.error);

    fetch(`${BASE}/api/swaps/pending`, { headers })
      .then(r => r.json())
      .then(setPendingSwaps)
      .catch(console.error);

    fetch(`${BASE}/api/users`, { headers })
      .then(r => r.json())
      .then(setAllUsers)
      .catch(console.error);
  }, [BASE]);

  // ---------- Actions ----------
  const approveLeave = async (id) => {
    try {
      const t = localStorage.getItem('token');
      const res = await fetch(`${BASE}/api/leaves/${id}/approve-manager`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${t}`,
          'Content-Type': 'application/json'
        }
      });
      if (!res.ok) throw new Error('Approve leave failed');
      setPendingLeaves(prev => prev.filter(l => l._id !== id));
    } catch (err) {
      console.error(err);
      alert('Failed to approve leave');
    }
  };

  const approveSwap = async (id) => {
    try {
      const t = localStorage.getItem('token');
      const res = await fetch(`${BASE}/api/swaps/${id}/approve`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${t}`,
          'Content-Type': 'application/json'
        }
      });
      if (!res.ok) throw new Error('Approve swap failed');
      setPendingSwaps(prev => prev.filter(s => s._id !== id));
    } catch (err) {
      console.error(err);
      alert('Failed to approve swap');
    }
  };

  const submitNewShift = async (e) => {
  e.preventDefault();

  // 1) Basic validation so we don't send empties
  if (!newShift.userId || !newShift.date) {
    alert("Please select a user and a date.");
    return;
  }

  // 2) Normalize date (HTML date -> ISO YYYY-MM-DD)
  // If your API expects full ISO, use new Date(newShift.date).toISOString()
  const isoDay = newShift.date; // already "YYYY-MM-DD" from <input type="date" />

  // 3) Choose ONE payload shape that your backend expects.
  //    Uncomment the one that matches your API and keep the others commented out.

  // a) Many backends:
  const payload = { userId: newShift.userId, date: isoDay };

  // b) If your API expects a different naming:
  // const payload = { user: newShift.userId, date: isoDay };

  // c) If it uses employeeId / shiftDate:
  // const payload = { employeeId: newShift.userId, shiftDate: isoDay };

  try {
    const t = localStorage.getItem("token");

    const res = await fetch(`${BASE}/api/shifts`, {
      method: "POST",
      headers: {
        "Authorization": `Bearer ${t}`,
        "Content-Type": "application/json",
        "Accept": "application/json"
      },
      body: JSON.stringify(payload)
    });

    // 4) Read response body for helpful error messages
    const contentType = res.headers.get("content-type") || "";
    const body = contentType.includes("application/json")
      ? await res.json().catch(() => ({}))
      : await res.text().catch(() => "");

    if (!res.ok) {
      console.error("Assign shift failed:", {
        url: res.url,
        status: res.status,
        statusText: res.statusText,
        response: body
      });
      const msg =
        (body && body.message) ||
        (typeof body === "string" ? body : "") ||
        `Failed to assign shift (HTTP ${res.status})`;
      alert(msg);
      return;
    }

    // 5) Success – add to UI and clear form
    const created = (body && body._id) ? body : (typeof body === "object" ? body : {});
    setAllShifts((prev) => [created, ...prev]);
    setNewShift({ userId: "", date: "" });
  } catch (err) {
    console.error(err);
    alert("Failed to assign shift");
  }
};


  return (
    <div className="container py-3">
      <h2 className="mb-3 fw-bold">Manager Dashboard</h2>

      <div className="d-grid gap-3">
        {/* All Shifts */}
        <section className="card shadow-sm p-3">
          <h3 className="mb-3 text-primary fw-bold">All Shifts</h3>
          <ul className="list-unstyled d-grid gap-2">
            {allShifts.map((s) => (
              <li
                key={s._id}
                className="d-flex flex-wrap align-items-center justify-content-between gap-2 border rounded-3 p-2"
              >
                <span>
                  {nameFrom(s)} — {dateStr(s.date || s.startDate)}
                </span>
              </li>
            ))}
          </ul>
        </section>

        {/* Assign New Shift */}
        <section className="card shadow-sm p-3">
          <h3 className="mb-3 text-primary fw-bold">Assign New Shift</h3>
          <form className="row g-2" onSubmit={submitNewShift}>
            <div className="col-12 col-sm-6">
              <select
                className="form-select"
                value={newShift.userId}
                onChange={(e) => setNewShift({ ...newShift, userId: e.target.value })}
                required
              >
                <option value="">Select user...</option>
                {allUsers.map((u) => (
                  <option key={u._id || u.id} value={u._id || u.id}>
                    {u.name || u.fullName || u.username || u.email}
                  </option>
                ))}
              </select>
            </div>
            <div className="col-12 col-sm-6">
              <input
                type="date"
                className="form-control"
                value={newShift.date}
                onChange={(e) => setNewShift({ ...newShift, date: e.target.value })}
                required
              />
            </div>
            <div className="col-12 d-flex justify-content-end">
              <button type="submit" className="btn btn-primary">Assign Shift</button>
            </div>
          </form>
        </section>

        {/* Pending Leave Approvals */}
        <section className="card shadow-sm p-3">
          <h3 className="mb-3 text-primary fw-bold">Pending Leave Approvals</h3>
          <ul className="list-unstyled d-grid gap-2">
            {pendingLeaves.map((l) => (
              <li
                key={l._id}
                className="d-flex flex-wrap align-items-center justify-content-between gap-2 border rounded-3 p-2"
              >
                <span>
                  {nameFrom(l)}: {l.startDate?.substring(0, 10)} to {l.endDate?.substring(0, 10)} — {l.status}
                </span>
                <div className="d-flex gap-2">
                  <button
                    className="btn btn-sm btn-primary"
                    onClick={() => approveLeave(l._id)}
                  >
                    Approve
                  </button>
                </div>
              </li>
            ))}
          </ul>
        </section>

        {/* Pending Swap Approvals */}
        <section className="card shadow-sm p-3">
          <h3 className="mb-3 text-primary fw-bold">Pending Swap Approvals</h3>
          <ul className="list-unstyled d-grid gap-2">
            {pendingSwaps.map((s) => (
              <li
                key={s._id}
                className="d-flex flex-wrap align-items-center justify-content-between gap-2 border rounded-3 p-2"
              >
                <span>
                  Swap between {swapName(s.initiator, s.initiatorId)} and {swapName(s.target, s.targetId)} (
                  {dateStr(s.initiatorShift?.date)} &nbsp;&&nbsp; {dateStr(s.targetShift?.date)})
                </span>
                <div className="d-flex gap-2">
                  <button
                    className="btn btn-sm btn-primary"
                    onClick={() => approveSwap(s._id)}
                  >
                    Approve
                  </button>
                </div>
              </li>
            ))}
          </ul>
        </section>
      </div>
    </div>
  );
}

export default ManagerDashboard;
