// src/components/Dashboard.jsx
import React, { useEffect, useMemo, useState } from "react";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

async function api(path, { method = "GET", body, token } = {}) {
  const res = await fetch(`${API_URL}${path}`, {
    method,
    headers: {
      "Content-Type": "application/json",
      ...(token ? { Authorization: `Bearer ${token}` } : {}),
    },
    ...(body ? { body: JSON.stringify(body) } : {}),
  });
  const data = await res.json().catch(() => ({}));
  if (!res.ok) throw new Error(data.message || res.statusText);
  return data;
}

function SectionCard({ title, subtitle, children, footer }) {
  return (
    <section className="rounded-2xl border border-slate-200 bg-white shadow-sm">
      <div className="border-b border-slate-100 px-5 py-4">
        <h2 className="text-xl font-semibold text-slate-800">{title}</h2>
        {subtitle && <p className="mt-1 text-sm text-slate-500">{subtitle}</p>}
      </div>
      <div className="px-5 py-5">{children}</div>
      {footer && <div className="border-t border-slate-100 px-5 py-4">{footer}</div>}
    </section>
  );
}

function Field({ label, children, help, error }) {
  return (
    <div className="grid gap-1.5">
      {label && <label className="text-sm font-medium text-slate-700">{label}</label>}
      {children}
      {help && !error && <p className="text-xs text-slate-500">{help}</p>}
      {error && <p className="text-xs text-rose-600">{error}</p>}
    </div>
  );
}

function InputBase({ className = "", ...props }) {
  return (
    <input
      className={
        "h-10 w-full rounded-lg border border-slate-300 bg-white px-3 text-sm text-slate-800 outline-none transition focus:border-blue-500 focus:ring-4 focus:ring-blue-100 " +
        className
      }
      {...props}
    />
  );
}

function SelectBase({ className = "", ...props }) {
  return (
    <select
      className={
        "h-10 w-full rounded-lg border border-slate-300 bg-white px-3 text-sm text-slate-800 outline-none transition focus:border-blue-500 focus:ring-4 focus:ring-blue-100 " +
        className
      }
      {...props}
    />
  );
}

function Button({ children, variant = "primary", className = "", ...props }) {
  const base =
    "inline-flex items-center justify-center rounded-lg px-4 py-2.5 text-sm font-medium transition focus:outline-none focus:ring-4 disabled:opacity-60";
  const styles =
    variant === "primary"
      ? "bg-blue-600 text-white hover:bg-blue-700 focus:ring-blue-100"
      : "bg-slate-100 text-slate-800 hover:bg-slate-200 focus:ring-slate-100";
  return (
    <button className={`${base} ${styles} ${className}`} {...props}>
      {children}
    </button>
  );
}

function Alert({ kind = "error", children }) {
  const styles =
    kind === "error"
      ? "bg-rose-50 text-rose-700 border border-rose-200"
      : "bg-emerald-50 text-emerald-700 border border-emerald-200";
  return (
    <div
      role="status"
      className={`rounded-lg px-3 py-2 text-sm ${styles}`}
      aria-live="polite"
    >
      {children}
    </div>
  );
}

export default function Dashboard() {
  // ---- auth token (adjust if stored differently) ----
  const [token] = useState(() => localStorage.getItem("token") || "");

  // ---- data for the page ----
  const [myShifts, setMyShifts] = useState([]);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [loadMsg, setLoadMsg] = useState("");

  // ---- request swap state ----
  const [yourShiftId, setYourShiftId] = useState("");
  const [targetUserId, setTargetUserId] = useState("");
  const [targetDate, setTargetDate] = useState(""); // yyyy-mm-dd
  const [targetShiftId, setTargetShiftId] = useState("");
  const [findingTarget, setFindingTarget] = useState(false);
  const [msg, setMsg] = useState({ type: "", text: "" });

  // ---------- load initial data ----------
  useEffect(() => {
    let cancelled = false;
    async function load() {
      try {
        setLoading(true);
        setLoadMsg("");
        const mine = await api(`/shifts?mine=1`, { token });

        // If /users is Manager-only, replace with your teammates endpoint.
        let people = [];
        try {
          people = await api(`/users`, { token });
        } catch {
          people = [];
        }

        if (!cancelled) {
          setMyShifts(Array.isArray(mine) ? mine : []);
          setUsers(Array.isArray(people) ? people : []);
          if (mine?.[0]?._id) setYourShiftId(mine[0]._id);
        }
      } catch (e) {
        if (!cancelled) setLoadMsg(e.message);
      } finally {
        if (!cancelled) setLoading(false);
      }
    }
    load();
    return () => {
      cancelled = true;
    };
  }, [token]);

  // ---------- resolve targetShiftId whenever user+date change ----------
  useEffect(() => {
    let cancelled = false;
    async function resolveTarget() {
      setMsg({ type: "", text: "" });
      setTargetShiftId("");
      if (!targetUserId || !targetDate) return;
      try {
        setFindingTarget(true);
        const list = await api(
          `/shifts?userId=${encodeURIComponent(
            targetUserId
          )}&date=${encodeURIComponent(targetDate)}`,
          { token }
        );
        if (!cancelled) {
          const found = Array.isArray(list) && list.length ? list[0] : null;
          setTargetShiftId(found ? found._id : "");
          if (!found) setMsg({ type: "error", text: "Selected user has no shift on that date." });
        }
      } catch (e) {
        if (!cancelled) setMsg({ type: "error", text: e.message });
      } finally {
        if (!cancelled) setFindingTarget(false);
      }
    }
    resolveTarget();
    return () => {
      cancelled = true;
    };
  }, [targetUserId, targetDate, token]);

  async function handleSwapSubmit(e) {
    e.preventDefault();
    setMsg({ type: "", text: "" });

    if (!yourShiftId || !targetUserId || !targetDate) {
      setMsg({ type: "error", text: "Please select: My shift, User to swap with, and Target date." });
      return;
    }
    if (!targetShiftId) {
      setMsg({ type: "error", text: "Selected user has no shift on the chosen date." });
      return;
    }

    try {
      const payload = { yourShiftId, targetUserId, targetShiftId, targetDate };
      const res = await api(`/shifts/swap`, { method: "POST", body: payload, token });
      setMsg({ type: "success", text: res.message || "Swap request created." });
    } catch (e) {
      setMsg({ type: "error", text: e.message || "Failed to create swap request." });
    }
  }

  const myShiftOptions = useMemo(
    () =>
      myShifts.map((s) => ({
        id: s._id,
        label: `${new Date(s.date).toLocaleDateString()} â€” ${s.user?.name || "Me"}`,
      })),
    [myShifts]
  );

  return (
    <div className="min-h-screen bg-slate-50">
      {/* Top bar */}
      <header className="sticky top-0 z-10 border-b border-slate-200 bg-white/90 backdrop-blur">
        <div className="mx-auto max-w-6xl px-4 py-4">
          <h1 className="text-2xl font-semibold text-slate-900">Leave &amp; Shift Manager</h1>
        </div>
      </header>

      {/* Content grid */}
      <main className="mx-auto max-w-6xl px-4 py-6 grid gap-6 md:grid-cols-2">
        {/* My Shifts */}
        <SectionCard
          title="My Shifts"
          subtitle="Your upcoming shifts (local time)."
          footer={
            loadMsg ? (
              <Alert kind="error">{loadMsg}</Alert>
            ) : (
              <p className="text-xs text-slate-500">Showing {myShifts.length} shift(s).</p>
            )
          }
        >
          {loading ? (
            <div className="space-y-2">
              <div className="h-4 w-40 animate-pulse rounded bg-slate-200" />
              <div className="h-4 w-56 animate-pulse rounded bg-slate-200" />
              <div className="h-4 w-48 animate-pulse rounded bg-slate-200" />
            </div>
          ) : (
            <ul className="divide-y divide-slate-100">
              {myShifts.map((s) => (
                <li key={s._id} className="flex items-center justify-between py-3">
                  <div className="flex items-center gap-3">
                    <span className="inline-flex items-center rounded-full bg-blue-50 px-2.5 py-1 text-xs font-medium text-blue-700">
                      {new Date(s.date).toLocaleDateString()}
                    </span>
                    <span className="text-sm text-slate-700">
                      {s.user?.name || "Me"}
                    </span>
                  </div>
                  <span className="text-xs text-slate-400">id: {s._id}</span>
                </li>
              ))}
              {!myShifts.length && (
                <li className="py-2 text-sm text-slate-500">No shifts found.</li>
              )}
            </ul>
          )}
        </SectionCard>

        {/* Request Swap */}
        <SectionCard
          title="Request Swap"
          subtitle="Pick your shift, a colleague, and the date you want to swap."
          footer={
            <div className="flex items-center justify-between">
              <p className="text-xs text-slate-500">
                Need help? Ensure the selected user actually has a shift on that date.
              </p>
              <div className="text-xs text-slate-400">
                {targetShiftId ? `Target shift: ${targetShiftId}` : ""}
              </div>
            </div>
          }
        >
          <form onSubmit={handleSwapSubmit} className="grid gap-4">
            <Field
              label="My shift"
              help="Choose the shift you want to swap away."
            >
              <SelectBase
                value={yourShiftId}
                onChange={(e) => setYourShiftId(e.target.value)}
              >
                {myShiftOptions.map((o) => (
                  <option key={o.id} value={o.id}>
                    {o.label}
                  </option>
                ))}
              </SelectBase>
            </Field>

            <div className="grid gap-4 md:grid-cols-2">
              <Field label="User to swap with" help="Pick the colleague you want to swap with.">
                <SelectBase
                  value={targetUserId}
                  onChange={(e) => setTargetUserId(e.target.value)}
                >
                  <option value="">Select a userâ€¦</option>
                  {users
                    .filter((u) => !!u?._id)
                    .map((u) => (
                      <option key={u._id} value={u._id}>
                        {u.name}
                      </option>
                    ))}
                </SelectBase>
              </Field>

              <Field label="Target date" help="Weâ€™ll look up that userâ€™s shift on this date.">
                <InputBase
                  type="date"
                  value={targetDate}
                  onChange={(e) => setTargetDate(e.target.value)}
                />
              </Field>
            </div>

            {findingTarget && (
              <Alert kind="success">Checking target shiftâ€¦</Alert>
            )}

            {msg.text && (
              <Alert kind={msg.type === "success" ? "success" : "error"}>{msg.text}</Alert>
            )}

            <div className="flex items-center gap-3 pt-2">
              <Button type="submit">Request Swap</Button>
              <Button
                type="button"
                variant="secondary"
                onClick={() => {
                  setTargetUserId("");
                  setTargetDate("");
                  setTargetShiftId("");
                  setMsg({ type: "", text: "" });
                }}
              >
                Clear
              </Button>
            </div>
          </form>
        </SectionCard>
      </main>
    </div>
  );
}
