import React, { useEffect, useState } from "react";

/**
 * EmployeeDashboard
 * - Shows My Shifts
 * - Request Leave (form)
 * - My Leave Requests
 * - (Optional) My Swap Requests (approve/cancel if your API supports)
 *
 * ⚠ If you already have logic in your file:
 *    - Keep your existing state/effects/handlers
 *    - Copy only the JSX wrappers and className attributes from below
 *    - The UI cards/rows/forms are styled with Bootstrap + your modern.css
 */
function EmployeeDashboard({ user }) {
  // ----- STATE (basic defaults; replace with your existing state if you have it) -----
  const [myShifts, setMyShifts] = useState([]);
  const [myLeaves, setMyLeaves] = useState([]);
  const [mySwaps, setMySwaps] = useState([]);

  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [reason, setReason] = useState("");

  const BASE = process.env.REACT_APP_API_BASE_URL || "";

  // ----- DATA LOAD (adjust endpoints to match your backend; or remove if you already load elsewhere) -----
  useEffect(() => {
    const t = localStorage.getItem("token");
    const headers = { Authorization: `Bearer ${t}` };

    // My shifts
    fetch(`${BASE}/api/shifts/my`, { headers })
      .then((r) => r.json())
      .then(setMyShifts)
      .catch(console.error);

    // My leaves
    fetch(`${BASE}/api/leaves/mine`, { headers })
      .then((r) => r.json())
      .then(setMyLeaves)
      .catch(console.error);

    // My swap requests (optional)
    fetch(`${BASE}/api/swaps/mine`, { headers })
      .then((r) => (r.ok ? r.json() : []))
      .then(setMySwaps)
      .catch(() => setMySwaps([]));
  }, [BASE]);

  // ----- HANDLERS (UI-safe; adjust paths if needed, or remove if you already have handlers) -----
  const submitLeave = async (e) => {
    e.preventDefault();
    try {
      const t = localStorage.getItem("token");
      const res = await fetch(`${BASE}/api/leaves`, {
        method: "POST",
        headers: {
          Authorization: `Bearer ${t}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ startDate, endDate, reason }),
      });
      if (!res.ok) throw new Error("Leave request failed");
      const created = await res.json();
      setMyLeaves((prev) => [created, ...prev]);
      setStartDate("");
      setEndDate("");
      setReason("");
    } catch (err) {
      console.error(err);
      alert("Failed to submit leave request");
    }
  };

  // Optional helpers if your API supports cancel/confirm:
  const cancelLeave = async (id) => {
    try {
      const t = localStorage.getItem("token");
      const res = await fetch(`${BASE}/api/leaves/${id}/cancel`, {
        method: "POST",
        headers: { Authorization: `Bearer ${t}` },
      });
      if (!res.ok) throw new Error("Cancel failed");
      setMyLeaves((prev) => prev.filter((l) => l._id !== id));
    } catch (e) {
      console.error(e);
      alert("Failed to cancel");
    }
  };

  const confirmSwap = async (id) => {
    try {
      const t = localStorage.getItem("token");
      const res = await fetch(`${BASE}/api/swaps/${id}/confirm`, {
        method: "POST",
        headers: { Authorization: `Bearer ${t}` },
      });
      if (!res.ok) throw new Error("Confirm failed");
      setMySwaps((prev) => prev.filter((s) => s._id !== id));
    } catch (e) {
      console.error(e);
      alert("Failed to confirm swap");
    }
  };

  const cancelSwap = async (id) => {
    try {
      const t = localStorage.getItem("token");
      const res = await fetch(`${BASE}/api/swaps/${id}/cancel`, {
        method: "POST",
        headers: { Authorization: `Bearer ${t}` },
      });
      if (!res.ok) throw new Error("Cancel failed");
      setMySwaps((prev) => prev.filter((s) => s._id !== id));
    } catch (e) {
      console.error(e);
      alert("Failed to cancel swap");
    }
  };

  return (
    <div className="container py-3">
      <h2 className="mb-3 fw-bold">Employee Dashboard</h2>

      <div className="d-grid gap-3">
        {/* ========= My Shifts ========= */}
        <section className="card shadow-sm p-3">
          <h3 className="mb-3 text-primary fw-bold">My Shifts</h3>
          <ul className="list-unstyled d-grid gap-2">
            {myShifts.map((s) => (
              <li
                key={s._id}
                className="d-flex flex-wrap align-items-center justify-content-between gap-2 border rounded-3 p-2"
              >
                <span>
                  {s.date
                    ? new Date(s.date).toLocaleDateString()
                    : s.startDate
                    ? new Date(s.startDate).toLocaleDateString()
                    : ""}{" "}
                  — {s.role || s.user?.role || "Shift"}
                </span>
              </li>
            ))}
          </ul>
        </section>

        {/* ========= Request Leave ========= */}
        <section className="card shadow-sm p-3">
          <h3 className="mb-3 text-primary fw-bold">Request Leave</h3>
          <form className="row g-2" onSubmit={submitLeave}>
            <div className="col-12 col-sm-6">
              <label htmlFor="start" className="form-label">
                Start date
              </label>
              <input
                id="start"
                type="date"
                className="form-control"
                value={startDate}
                onChange={(e) => setStartDate(e.target.value)}
                required
              />
            </div>
            <div className="col-12 col-sm-6">
              <label htmlFor="end" className="form-label">
                End date
              </label>
              <input
                id="end"
                type="date"
                className="form-control"
                value={endDate}
                onChange={(e) => setEndDate(e.target.value)}
                required
              />
            </div>
            <div className="col-12">
              <label htmlFor="reason" className="form-label">
                Reason (optional)
              </label>
              <textarea
                id="reason"
                rows={3}
                className="form-control"
                placeholder="e.g., personal leave"
                value={reason}
                onChange={(e) => setReason(e.target.value)}
              />
            </div>
            <div className="col-12 d-flex justify-content-end">
              <button type="submit" className="btn btn-primary">
                Submit Request
              </button>
            </div>
          </form>
        </section>

        {/* ========= My Leave Requests ========= */}
        <section className="card shadow-sm p-3">
          <h3 className="mb-3 text-primary fw-bold">My Leave Requests</h3>
          <ul className="list-unstyled d-grid gap-2">
            {myLeaves.map((l) => (
              <li
                key={l._id}
                className="d-flex flex-wrap align-items-center justify-content-between gap-2 border rounded-3 p-2"
              >
                <span>
                  {l.startDate?.substring(0, 10)} → {l.endDate?.substring(0, 10)} —{" "}
                  {l.status}
                </span>
                <div className="d-flex gap-2">
                  {/* Show cancel only for pending, if that matches your rules */}
                  {l.status?.toLowerCase() === "pending" && (
                    <button
                      className="btn btn-sm btn-outline-danger"
                      onClick={() => cancelLeave(l._id)}
                    >
                      Cancel
                    </button>
                  )}
                </div>
              </li>
            ))}
          </ul>
        </section>

        {/* ========= My Swap Requests (optional) ========= */}
        <section className="card shadow-sm p-3">
          <h3 className="mb-3 text-primary fw-bold">My Swap Requests</h3>
          <ul className="list-unstyled d-grid gap-2">
            {mySwaps.map((s) => (
              <li
                key={s._id}
                className="d-flex flex-wrap align-items-center justify-content-between gap-2 border rounded-3 p-2"
              >
                <span>
                  Swap with {s.target?.name || "teammate"} (
                  {s.myShiftDate
                    ? new Date(s.myShiftDate).toLocaleDateString()
                    : ""}
                  {"  "}⇄{"  "}
                  {s.theirShiftDate
                    ? new Date(s.theirShiftDate).toLocaleDateString()
                    : ""}
                  ) — {s.status}
                </span>
                <div className="d-flex gap-2">
                  {s.status?.toLowerCase() === "pending" && (
                    <>
                      <button
                        className="btn btn-sm btn-primary"
                        onClick={() => confirmSwap(s._id)}
                      >
                        Confirm
                      </button>
                      <button
                        className="btn btn-sm btn-outline-danger"
                        onClick={() => cancelSwap(s._id)}
                      >
                        Cancel
                      </button>
                    </>
                  )}
                </div>
              </li>
            ))}
          </ul>
        </section>
      </div>
    </div>
  );
}

export default EmployeeDashboard;
