import React, { useEffect, useMemo, useState } from "react";

/* ===== API paths (match your backend) ===== */
const API = {
  shifts: {
    list: "/api/shifts",      // GET (employees get own shifts)
    swap: "/api/shifts/swap", // POST (employee proposes swap)
  },
  leaves: {
    list: "/api/leaves",      // GET
    create: "/api/leaves",    // POST
  },
};

/* Safe body reader (JSON or text) */
const readBody = async (res) => {
  const ct = res.headers.get("content-type") || "";
  if (ct.includes("application/json")) {
    try { return await res.json(); } catch { return null; }
  }
  try { return await res.text(); } catch { return null; }
};

/* Flexible date helpers (handles various field names) */
const dateFrom = (obj) =>
  (obj && (obj.startDate || obj.date || obj.from || obj.start || obj.shiftDate)) || null;
const dateTo = (obj) =>
  (obj && (obj.endDate || obj.date || obj.to || obj.end || obj.shiftDate)) || null;

const fmt = (d) => (d ? new Date(d).toLocaleDateString() : "");

/* ---------- Component ---------- */
function EmployeeDashboard({ user }) {
  const [myShifts, setMyShifts] = useState([]);
  const [myLeaves, setMyLeaves] = useState([]);

  // Leave form
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [reason, setReason] = useState("");

  // Swap form
  const [swapForm, setSwapForm] = useState({
    myShiftId: "",
    targetShiftId: "",  // Option A: paste a shift id to swap with
    targetUserId: "",   // Option B: enter a target user id
    targetDate: "",     // Option B: and a date of their shift
  });

  // API base (use .env.local -> REACT_APP_API_BASE_URL=http://localhost:5000, or CRA proxy)
  const BASE = process.env.REACT_APP_API_BASE_URL || "";

  // My id (from prop or localStorage)
  const meId = useMemo(() => {
    if (user && user._id) return user._id;
    try {
      const stored = JSON.parse(localStorage.getItem("user") || "{}");
      return stored && stored._id ? stored._id : null;
    } catch {
      return null;
    }
  }, [user]);

  /* Load shifts + leaves (robust to response shapes) */
  const loadEmployeeData = async () => {
    try {
      const t = localStorage.getItem("token");
      const headers = { Authorization: `Bearer ${t}` };

      const [shiftsRes, leavesRes] = await Promise.all([
        fetch(`${BASE}${API.shifts.list}`, { headers }),
        fetch(`${BASE}${API.leaves.list}`, { headers }),
      ]);

      const [shiftsRaw, leavesRaw] = await Promise.all([
        readBody(shiftsRes),
        readBody(leavesRes),
      ]);

      // Shifts: employees get only their own
      setMyShifts(Array.isArray(shiftsRaw) ? shiftsRaw : []);

      // Leaves: accept [] or {leaves:[...]} or {data:[...]}
      const leavesArr = Array.isArray(leavesRaw)
        ? leavesRaw
        : Array.isArray(leavesRaw?.leaves)
        ? leavesRaw.leaves
        : Array.isArray(leavesRaw?.data)
        ? leavesRaw.data
        : [];

      const mine = meId
        ? leavesArr.filter(
            (l) =>
              l?.user?._id === meId ||
              l?.userId === meId ||
              (typeof l?.user === "string" && l.user === meId)
          )
        : leavesArr;

      setMyLeaves(mine);
    } catch (e) {
      console.error(e);
      setMyShifts([]);
      setMyLeaves([]);
    }
  };

  useEffect(() => {
    loadEmployeeData();
  }, [BASE]);

  /* Submit leave -> re-fetch so it shows immediately */
  const submitLeave = async (e) => {
    e.preventDefault();
    try {
      const t = localStorage.getItem("token");
      const res = await fetch(`${BASE}${API.leaves.create}`, {
        method: "POST",
        headers: {
          Authorization: `Bearer ${t}`,
          "Content-Type": "application/json",
          Accept: "application/json",
        },
        body: JSON.stringify({ startDate, endDate, reason }),
      });

      const body = await readBody(res);
      if (!res.ok) {
        console.error("Leave submit failed:", res.status, body);
        alert(
          (body && body.message) ||
            (typeof body === "string" ? body : `Failed to submit leave (HTTP ${res.status})`)
        );
        return;
      }

      setStartDate("");
      setEndDate("");
      setReason("");
      await loadEmployeeData();
    } catch (err) {
      console.error(err);
      alert("Failed to submit leave");
    }
  };

  /* Submit swap request */
  const submitSwap = async (e) => {
    e.preventDefault();

    if (!swapForm.myShiftId) {
      alert("Please select your shift to swap.");
      return;
    }
    if (!swapForm.targetShiftId && !(swapForm.targetUserId && swapForm.targetDate)) {
      alert("Provide either a Target Shift ID, or a Target User ID + Target Date.");
      return;
    }

    // Build payload for your backend's /api/shifts/swap
    // Most backends expect: { initiatorShiftId, targetShiftId }
    // If yours expects different keys (e.g., fromShiftId/toShiftId or targetUserId/date),
    // change ONLY this object accordingly.
    const payload = swapForm.targetShiftId
      ? {
          initiatorShiftId: swapForm.myShiftId,
          targetShiftId: swapForm.targetShiftId,
        }
      : {
          initiatorShiftId: swapForm.myShiftId,
          targetUserId: swapForm.targetUserId,
          date: swapForm.targetDate, // if your API uses 'targetDate' or 'shiftDate', rename here
        };

    try {
      const t = localStorage.getItem("token");
      const res = await fetch(`${BASE}${API.shifts.swap}`, {
        method: "POST",
        headers: {
          Authorization: `Bearer ${t}`,
          "Content-Type": "application/json",
          Accept: "application/json",
        },
        body: JSON.stringify(payload),
      });

      const body = await readBody(res);
      if (!res.ok) {
        console.error("Swap request failed:", res.status, body);
        alert(
          (body && body.message) ||
            (typeof body === "string" ? body : `Failed to request swap (HTTP ${res.status})`)
        );
        return;
      }

      // Success
      setSwapForm({ myShiftId: "", targetShiftId: "", targetUserId: "", targetDate: "" });
      alert("Swap request sent!");
    } catch (err) {
      console.error(err);
      alert("Failed to request swap");
    }
  };

  // Helpful label for shift options
  const shiftLabel = (s) => {
    const d =
      s.date || s.shiftDate || s.startDate || s.assignedDate || s.scheduledFor || s.day || s.dayDate;
    const ds = d ? new Date(d).toLocaleDateString() : "(no date)";
    const id = s._id ? ` – ${s._id.slice(-6)}` : "";
    return `${ds}${id}`;
  };

  return (
    <div className="container py-3">
      {/* My Shifts */}
      <section className="card shadow-sm p-3 mb-3">
        <h3 className="mb-3 text-primary fw-bold">My Shifts</h3>
        <ul className="list-unstyled d-grid gap-2">
          {myShifts.map((s, i) => (
            <li
              key={s._id || `shift-${i}`}
              className="d-flex flex-wrap align-items-center justify-content-between gap-2 border rounded-3 p-2"
            >
              <span>{shiftLabel(s)}</span>
            </li>
          ))}
          {myShifts.length === 0 && <li className="text-muted">No shifts found.</li>}
        </ul>
      </section>

      {/* Request Swap */}
      <section className="card shadow-sm p-3 mb-3">
        <h3 className="mb-3 text-primary fw-bold">Request Swap</h3>
        <form className="row g-3" onSubmit={submitSwap}>
          <div className="col-12 col-md-6">
            <label className="form-label">My shift</label>
            <select
              className="form-select"
              value={swapForm.myShiftId}
              onChange={(e) => setSwapForm({ ...swapForm, myShiftId: e.target.value })}
              required
            >
              <option value="">Select your shift…</option>
              {myShifts.map((s) => (
                <option key={s._id} value={s._id}>
                  {shiftLabel(s)}
                </option>
              ))}
            </select>
            <div className="form-text">Choose the shift you want to swap away.</div>
          </div>

          <div className="col-12">
            <div className="small text-secondary">Provide <b>either</b> a Target Shift ID, <b>or</b> a Target User ID + Date.</div>
          </div>

          {/* Option A — direct target shift id */}
          <div className="col-12 col-md-6">
            <label className="form-label">Target shift ID (optional)</label>
            <input
              type="text"
              className="form-control"
              placeholder="e.g. 652adf…"
              value={swapForm.targetShiftId}
              onChange={(e) => setSwapForm({ ...swapForm, targetShiftId: e.target.value })}
            />
            <div className="form-text">Paste the other person’s shift ID if you know it.</div>
          </div>

          {/* Option B — target user and date */}
          <div className="col-12 col-md-4">
            <label className="form-label">Target user ID (optional)</label>
            <input
              type="text"
              className="form-control"
              placeholder="user id"
              value={swapForm.targetUserId}
              onChange={(e) => setSwapForm({ ...swapForm, targetUserId: e.target.value })}
            />
          </div>
          <div className="col-12 col-md-2">
            <label className="form-label">Target date</label>
            <input
              type="date"
              className="form-control"
              value={swapForm.targetDate}
              onChange={(e) => setSwapForm({ ...swapForm, targetDate: e.target.value })}
            />
          </div>

          <div className="col-12 d-flex justify-content-end">
            <button type="submit" className="btn btn-primary">Request Swap</button>
          </div>
        </form>
      </section>

      {/* Request Leave */}
      <section className="card shadow-sm p-3 mb-3">
        <h3 className="mb-3 text-primary fw-bold">Request Leave</h3>
        <form className="row g-2" onSubmit={submitLeave}>
          <div className="col-12 col-sm-6">
            <label htmlFor="start" className="form-label">Start date</label>
            <input
              id="start"
              type="date"
              className="form-control"
              value={startDate}
              onChange={(e) => setStartDate(e.target.value)}
              required
            />
          </div>
          <div className="col-12 col-sm-6">
            <label htmlFor="end" className="form-label">End date</label>
            <input
              id="end"
              type="date"
              className="form-control"
              value={endDate}
              onChange={(e) => setEndDate(e.target.value)}
              required
            />
          </div>
          <div className="col-12">
            <label htmlFor="reason" className="form-label">Reason (optional)</label>
            <textarea
              id="reason"
              rows={3}
              className="form-control"
              placeholder="e.g., personal leave"
              value={reason}
              onChange={(e) => setReason(e.target.value)}
            />
          </div>
          <div className="col-12 d-flex justify-content-end">
            <button type="submit" className="btn btn-primary">Submit Request</button>
          </div>
        </form>
      </section>

      {/* My Leave Requests */}
      <section className="card shadow-sm p-3">
        <h3 className="mb-3 text-primary fw-bold">My Leave Requests</h3>
        <ul className="list-unstyled d-grid gap-2">
          {myLeaves.map((l) => (
            <li
              key={l._id}
              className="d-flex flex-wrap align-items-center justify-content-between gap-2 border rounded-3 p-2"
            >
              <span>
                {fmt(dateFrom(l))} &rarr; {fmt(dateTo(l))} — {l.status || "pending"}
              </span>
            </li>
          ))}
          {myLeaves.length === 0 && (
            <li className="text-muted">No leave requests yet.</li>
          )}
        </ul>
      </section>
    </div>
  );
}

export default EmployeeDashboard;
