import React, { useEffect, useMemo, useState } from "react";

/** Centralize API paths to match your backend exactly */
const API = {
  shifts: {
    list: "/api/shifts",                         // GET
    create: "/api/shifts/assign",                // POST (manager)
    swapList: "/api/shifts/swap-requests",       // GET (manager/TL)
    approveSwap: (id) => `/api/shifts/swap/${id}/approve`, // PATCH (manager)
  },
  leaves: {
    list: "/api/leaves",                         // GET (filter pending on client)
    approveManager: (id) => `/api/leaves/${id}/approve-manager`, // PATCH (manager)
  },
  users: { list: "/api/users" },                 // GET (manager)
};

/** Safely read a response as JSON or text (so 404 HTML doesnâ€™t crash JSON.parse) */
const readBody = async (res) => {
  const ct = res.headers.get("content-type") || "";
  if (ct.includes("application/json")) {
    try { return await res.json(); } catch { return null; }
  }
  try { return await res.text(); } catch { return null; }
};

function ManagerDashboard() {
  const [allShifts, setAllShifts] = useState([]);
  const [pendingLeaves, setPendingLeaves] = useState([]);
  const [pendingSwaps, setPendingSwaps] = useState([]);
  const [newShift, setNewShift] = useState({ userId: "", date: "" });
  const [allUsers, setAllUsers] = useState([]);

  const BASE = process.env.REACT_APP_API_BASE_URL || ""; // or use CRA proxy

  // Build a map of userId -> displayName for resolving names on lists
  const userMap = useMemo(() => {
    const m = {};
    for (const u of allUsers || []) {
      const name = u?.name || u?.fullName || u?.username || u?.email || "User";
      if (u?._id) m[u._id] = name;
      if (u?.id)  m[u.id]  = name; // just in case server returns id
    }
    return m;
  }, [allUsers]);

  const dateStr = (d) => (d ? new Date(d).toLocaleDateString() : "");

  /** Resolve a person's name from various possible shapes/fields */
  const nameFrom = (item) => {
    // nested objects
    const u = item?.user || item?.employee || item?.owner || null;
    if (u?.name || u?.fullName || u?.username || u?.email) {
      return u.name || u.fullName || u.username || u.email;
    }
    // id-style fields
    const possibleIds = [
      item?.userId,
      item?.employeeId,
      item?.ownerId,
      typeof item?.user === "string" ? item.user : null,
    ].filter(Boolean);
    for (const id of possibleIds) {
      if (userMap[id]) return userMap[id];
    }
    // flat name fields
    return item?.userName || item?.employeeName || item?.createdByName || "User";
  };

  const swapName = (obj, idKey) => {
    if (obj?.name || obj?.fullName || obj?.username || obj?.email) {
      return obj.name || obj.fullName || obj.username || obj.email;
    }
    if (obj?._id && userMap[obj._id]) return userMap[obj._id];
    if (idKey && userMap[idKey]) return userMap[idKey];
    return "User";
  };

  // -------- Load data --------
  useEffect(() => {
    const t = localStorage.getItem("token");
    const headers = { Authorization: `Bearer ${t}` };

    // Shifts
    fetch(`${BASE}${API.shifts.list}`, { headers })
      .then(readBody)
      .then((data) => Array.isArray(data) ? data : [])
      .then(setAllShifts)
      .catch(console.error);

    // Leaves (get ALL then filter to pending client-side)
    fetch(`${BASE}${API.leaves.list}`, { headers })
      .then(readBody)
      .then((data) => Array.isArray(data) ? data : [])
      .then((arr) => arr.filter((l) => (l.status || "").toLowerCase() === "pending"))
      .then(setPendingLeaves)
      .catch(console.error);

    // Swap requests
    fetch(`${BASE}${API.shifts.swapList}`, { headers })
      .then(readBody)
      .then((data) => Array.isArray(data) ? data : [])
      .then(setPendingSwaps)
      .catch(console.error);

    // Users for assign dropdown
    fetch(`${BASE}${API.users.list}`, { headers })
      .then(readBody)
      .then((data) => Array.isArray(data) ? data : [])
      .then(setAllUsers)
      .catch(console.error);
  }, [BASE]);

  // -------- Actions --------
  const submitNewShift = async (e) => {
    e.preventDefault();
    try {
      const t = localStorage.getItem("token");
      const res = await fetch(`${BASE}${API.shifts.create}`, {
        method: "POST",
        headers: {
          Authorization: `Bearer ${t}`,
          "Content-Type": "application/json",
          Accept: "application/json",
        },
        body: JSON.stringify({ userId: newShift.userId, date: newShift.date }),
      });

      const body = await readBody(res);
      if (!res.ok) {
        console.error("Assign shift failed:", res.status, body);
        alert((body && body.message) || (typeof body === "string" ? body : `Failed to assign shift (HTTP ${res.status})`));
        return;
      }

      // Optimistic UI: if server returns created shift, prepend; otherwise refetch
      if (body && typeof body === "object") {
        setAllShifts((prev) => [body, ...prev]);
      } else {
        // fallback: refetch list quietly
        const t2 = localStorage.getItem("token");
        const headers = { Authorization: `Bearer ${t2}` };
        fetch(`${BASE}${API.shifts.list}`, { headers })
          .then(readBody)
          .then((data) => Array.isArray(data) ? data : [])
          .then(setAllShifts)
          .catch(console.error);
      }
      setNewShift({ userId: "", date: "" });
    } catch (err) {
      console.error(err);
      alert("Failed to assign shift");
    }
  };

  const approveLeave = async (id) => {
    try {
      const t = localStorage.getItem("token");
      const res = await fetch(`${BASE}${API.leaves.approveManager(id)}`, {
        method: "PATCH",
        headers: { Authorization: `Bearer ${t}` },
      });
      const body = await readBody(res);
      if (!res.ok) {
        console.error("Approve leave failed:", res.status, body);
        alert((body && body.message) || "Failed to approve leave");
        return;
      }
      setPendingLeaves((prev) => prev.filter((l) => l._id !== id));
    } catch (e) {
      console.error(e);
      alert("Failed to approve leave");
    }
  };

  const approveSwap = async (id) => {
    try {
      const t = localStorage.getItem("token");
      const res = await fetch(`${BASE}${API.shifts.approveSwap(id)}`, {
        method: "PATCH",
        headers: { Authorization: `Bearer ${t}` },
      });
      const body = await readBody(res);
      if (!res.ok) {
        console.error("Approve swap failed:", res.status, body);
        alert((body && body.message) || "Failed to approve swap");
        return;
      }
      setPendingSwaps((prev) => prev.filter((s) => s._id !== id));
    } catch (e) {
      console.error(e);
      alert("Failed to approve swap");
    }
  };

  return (
    <div className="container py-3">
      <h2 className="mb-3 fw-bold">Manager Dashboard</h2>

      <div className="d-grid gap-3">
        {/* All Shifts */}
        <section className="card shadow-sm p-3">
          <h3 className="mb-3 text-primary fw-bold">All Shifts</h3>
          <ul className="list-unstyled d-grid gap-2">
            {allShifts.map((s) => (
              <li
                key={s._id}
                className="d-flex flex-wrap align-items-center justify-content-between gap-2 border rounded-3 p-2"
              >
                <span>
                  {nameFrom(s)} â€” {dateStr(s.date || s.startDate)}
                </span>
              </li>
            ))}
          </ul>
        </section>

        {/* Assign New Shift */}
        <section className="card shadow-sm p-3">
          <h3 className="mb-3 text-primary fw-bold">Assign New Shift</h3>
          <form className="row g-2" onSubmit={submitNewShift}>
            <div className="col-12 col-sm-6">
              <select
                className="form-select"
                value={newShift.userId}
                onChange={(e) => setNewShift({ ...newShift, userId: e.target.value })}
                required
              >
                <option value="">Select user...</option>
                {allUsers.map((u) => (
                  <option key={u._id || u.id} value={u._id || u.id}>
                    {u.name || u.fullName || u.username || u.email}
                  </option>
                ))}
              </select>
            </div>
            <div className="col-12 col-sm-6">
              <input
                type="date"
                className="form-control"
                value={newShift.date}
                onChange={(e) => setNewShift({ ...newShift, date: e.target.value })}
                required
              />
            </div>
            <div className="col-12 d-flex justify-content-end">
              <button type="submit" className="btn btn-primary">Assign Shift</button>
            </div>
          </form>
        </section>

        {/* Pending Leave Approvals */}
        <section className="card shadow-sm p-3">
          <h3 className="mb-3 text-primary fw-bold">Pending Leave Approvals</h3>
          <ul className="list-unstyled d-grid gap-2">
            {pendingLeaves.map((l) => (
              <li
                key={l._id}
                className="d-flex flex-wrap align-items-center justify-content-between gap-2 border rounded-3 p-2"
              >
                <span>
                  {nameFrom(l)}: {l.startDate?.substring(0, 10)} to {l.endDate?.substring(0, 10)} â€” {l.status}
                </span>
                <div className="d-flex gap-2">
                  <button
                    className="btn btn-sm btn-primary"
                    onClick={() => approveLeave(l._id)}
                  >
                    Approve
                  </button>
                </div>
              </li>
            ))}
          </ul>
        </section>

        {/* Pending Swap Approvals */}
        <section className="card shadow-sm p-3">
          <h3 className="mb-3 text-primary fw-bold">Pending Swap Approvals</h3>
          <ul className="list-unstyled d-grid gap-2">
            {pendingSwaps.map((s) => (
              <li
                key={s._id}
                className="d-flex flex-wrap align-items-center justify-content-between gap-2 border rounded-3 p-2"
              >
                <span>
                  Swap between {swapName(s.initiator, s.initiatorId)} and {swapName(s.target, s.targetId)} (
                  {dateStr(s.initiatorShift?.date)} &nbsp;&&nbsp; {dateStr(s.targetShift?.date)})
                </span>
                <div className="d-flex gap-2">
                  <button
                    className="btn btn-sm btn-primary"
                    onClick={() => approveSwap(s._id)}
                  >
                    Approve
                  </button>
                </div>
              </li>
            ))}
          </ul>
        </section>
      </div>
    </div>
  );
}

export default ManagerDashboard;
